    /* declare data array to record data into text file */
    String[] data = new String[4];
    String password, conPass;
    final String fileLocation = dataIO.GET_USER_FILE();
    /* End of declaration */

    private boolean validateResult(String username, String email, String password, String confirmPassword) {
        if (!User.validateUsername(username)) {
            messageHandling.errorUsername();
            usernameField.setText("");
            return false;
        } 

        if (!User.validateEmail(email)) {
            messageHandling.errorEmail();
            emailField.setText("");
            return false;
        }

        if (!User.isEmailUnique(email)) {
            messageHandling.repeatedEmail();
            emailField.setText("");
            return false;
        }

        if (!User.validatePassword(password)) {
            messageHandling.errorPassword();
            passwordField.setText("");
            confirmPasswordField.setText("");
            return false;
        }

        if (!password.equals(confirmPassword)) {
            JOptionPane.showMessageDialog(null, "Password does not match", "Error", JOptionPane.ERROR_MESSAGE);
            confirmPasswordField.setText("");
            return false;
        }

        return true;

    }


    /* Start of username field */
    private void usernameFieldActionPerformed(java.awt.event.ActionEvent evt) {
        usernameField.getText();
        data[0] = usernameField.getText();
    }
    /* End of username field */


    /* Start of email field */
    private void emailFieldActionPerformed(java.awt.event.ActionEvent evt) {
        emailField.getText();
        data[1] = emailField.getText();
    }
    /* End of email field */


    /* Start of password field */
    private void passwordFieldActionPerformed(java.awt.event.ActionEvent evt) {
        char[] passwordChars = passwordField.getPassword();
        
        // Convert the password character array to String
        password = new String(passwordChars);
        
        // Store the password in the data array
        data[2] = password;
    }
    /* End of password field */


    /* Start of confirm password field */
    private void confirmPasswordFieldActionPerformed(java.awt.event.ActionEvent evt) {
        char[] conPasswordChars = confirmPasswordField.getPassword();

        conPass = new String(conPasswordChars);

        data[3] = conPass;

    }
    /* End of confirm password field */


    /* Register button */
    private void registerButtonActionPerformed(java.awt.event.ActionEvent evt) {
        usernameFieldActionPerformed(evt);
        emailFieldActionPerformed(evt);
        passwordFieldActionPerformed(evt);
        confirmPasswordFieldActionPerformed(evt);
        
    if (validateResult(data[0], data[1], data[2], data[3])) {
        try {
            data[3] = "customer";
            dataIO.writeData(data, fileLocation); 
            JOptionPane.showMessageDialog(null, "Account created successfully", "Success", JOptionPane.INFORMATION_MESSAGE);
            pageSwitch.switchPage(this, new Login());
        } catch (Exception e) {
            e.printStackTrace();
        }

        }
    }

    /* End of register button */


    /* Back to login button */
    private void backToLoginActionPerformed(java.awt.event.ActionEvent evt) {
        pageSwitch.switchPage(this, new Login());
    }
    /* End of back to login button */





